// Read EVENT_TIMELINE.jsonl (last 6 hours)
const fs = require('fs');

const timelineFile = 'C:\\Users\\edri2\\Desktop\\AI\\ai-os\\truth-layer\\timeline\\EVENT_TIMELINE.jsonl';
const sixHoursAgo = new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString();

let events = [];

if (fs.existsSync(timelineFile)) {
  const lines = fs.readFileSync(timelineFile, 'utf-8').split('\n');

  for (const line of lines) {
    if (!line.trim()) continue;

    try {
      const event = JSON.parse(line);

      if (event.ts_utc >= sixHoursAgo) {
        events.push(event);
      }
    } catch (e) {
      console.error('Failed to parse line:', line);
    }
  }
}

return {
  json: {
    events: events,
    events_count: events.length,
    time_window: {
      start: sixHoursAgo,
      end: new Date().toISOString()
    }
  }
};

// Load Judge prompt
const fs = require('fs');

const promptFile = 'C:\\Users\\edri2\\Desktop\\AI\\ai-os\\prompts\\judge_agent_prompt.md';
const judgePrompt = fs.readFileSync(promptFile, 'utf-8');

const events = $input.first().json.events;
const timeWindow = $input.first().json.time_window;

const fullPrompt = judgePrompt + '\n\n---\n\n## Events to Analyze\n\n```json\n' + JSON.stringify(events, null, 2) + '\n```\n\nPlease analyze these ' + events.length + ' events and return your FauxPas_Report in JSON format.';

return {
  json: {
    prompt: fullPrompt,
    events_count: events.length,
    time_window: timeWindow
  }
};

// Parse GPT response and write FauxPas_Report
const fs = require('fs');

const gptResponse = $input.first().json.choices[0].message.content;
const report = JSON.parse(gptResponse);

const now = new Date();
const timestamp = now.toISOString().replace(/:/g, '-').split('.')[0];
const reportFile = `C:\\Users\\edri2\\Desktop\\AI\\ai-os\\truth-layer\\drift\\faux_pas\\FP-${timestamp}.json`;

const dir = 'C:\\Users\\edri2\\Desktop\\AI\\ai-os\\truth-layer\\drift\\faux_pas';
if (!fs.existsSync(dir)) {
  fs.mkdirSync(dir, { recursive: true });
}

fs.writeFileSync(reportFile, JSON.stringify(report, null, 2), 'utf-8');

console.log('FauxPas Report:', reportFile);
console.log('Summary:', report.summary);

return {
  json: {
    report_file: reportFile,
    report: report,
    has_errors: report.faux_pas_detected && report.faux_pas_detected.length > 0
  }
};
