// Parse Langfuse traces into events for Judge
const traces = $input.first().json.data || [];

const events = traces.map(trace => {
  return {
    trace_id: trace.id,
    name: trace.name,
    timestamp: trace.timestamp,
    user_id: trace.userId,
    tags: trace.tags || [],
    metadata: trace.metadata || {},
    status: trace.status || 'success',
    // Extract input/output if available
    input: trace.input,
    output: trace.output,
    // Metrics
    duration_ms: trace.duration,
    cost_usd: trace.cost
  };
});

return [
  {
    json: {
      events: events,
      events_count: events.length,
      time_window: {
        start: new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString(),
        end: new Date().toISOString()
      }
    }
  }
];

// Load Judge prompt
const fs = require('fs');
const path = require('path');

const promptFile = '/workspace/prompts/judge_agent_prompt.md';
const judgePrompt = fs.readFileSync(promptFile, 'utf-8');

const events = $input.first().json.events;
const timeWindow = $input.first().json.time_window;

// Build the full prompt
const fullPrompt = judgePrompt + '\n\n---\n\n## Events to Analyze (from Langfuse)\n\n```json\n' + JSON.stringify(events, null, 2) + '\n```\n\nPlease analyze these ' + events.length + ' events and return your FauxPas_Report in JSON format.';

return [
  {
    json: {
      prompt: fullPrompt,
      events_count: events.length,
      time_window: timeWindow
    }
  }
];

// Parse GPT-4o response and write FauxPas_Report
const fs = require('fs');
const path = require('path');

const gptResponse = $input.first().json.choices[0].message.content;
const report = JSON.parse(gptResponse);

// Generate report filename
const now = new Date();
const timestamp = now.toISOString().replace(/:/g, '-').split('.')[0];
const reportFile = `/workspace/truth-layer/drift/faux_pas/FP-${timestamp}.json`;

// Write report
fs.writeFileSync(reportFile, JSON.stringify(report, null, 2), 'utf-8');

// Also write to Langfuse as score/feedback
// (Optional: send back to Langfuse for trace scoring)

// Log to console for monitoring
console.log('FauxPas Report:', reportFile);
console.log('Summary:', report.summary);

return [
  {
    json: {
      report_file: reportFile,
      report: report,
      has_errors: report.faux_pas_detected && report.faux_pas_detected.length > 0
    }
  }
];
